import 'package:hive/hive.dart';import 'package:textdetection/Config/Route/app_route.dart';part 'hive_app_model.g.dart';@HiveType(typeId: 0)class HiveAppModel extends HiveObject {  @HiveField(0)  String? _token;  @HiveField(1)  String? _id;  @HiveField(2)  String? _refreshToken;  @HiveField(3)  bool _onboarding = true;  // Constructor  HiveAppModel({    String? token,    String? id,    String? refreshToken,    bool? onboarding,  })  : _token = token,        _id = id,        _refreshToken = refreshToken,        _onboarding = onboarding ?? true;  // Getters and setters  String? get token => _token;  set token(String? value) => _token = value;  String? get id => _id;  set id(String? value) => _id = value;  String? get refreshToken => _refreshToken;  set refreshToken(String? value) => _refreshToken = value;  bool get onboarding => _onboarding;  set onboarding(bool value) => _onboarding = value;  // Determine the starting screen based on onboarding and token status  String get startScreen {    if (!_onboarding) {      if (_token?.isNotEmpty ?? false) {        return AppRoutes.homeScreen;      } else {        return AppRoutes.loginScreen;      }    } else {      return AppRoutes.onboarding;    }  }  // Copy constructor for creating a new instance with optional modifications  HiveAppModel copyWith({    String? token,    String? id,    String? refreshToken,    bool? onboarding,  }) {    return HiveAppModel(      token: token ?? _token,      id: id ?? _id,      refreshToken: refreshToken ?? _refreshToken,      onboarding: onboarding ?? _onboarding,    );  }}