import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import '../utils/ResponsiveServices/responsive-design-service.dart';import 'colors.dart';class AppTextStyles {  static style30() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 30,        tablet: 34,        desktop: 38,      ),      FontWeight.w700,      AppColors.deepGray,      0.0);  static style16xW700() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 16,        tablet: 18,        desktop: 20,      ),      FontWeight.w700,      AppColors.darkGray,      0.0);  static style16xW700cornFlowerPrimary() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 16,        tablet: 18,        desktop: 20,      ),      FontWeight.w700,      AppColors.cornFlowerPrimary,      0.0);  static style16xW500() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 16,        tablet: 18,        desktop: 20,      ),      FontWeight.w500,      AppColors.darkBlueGray,      0.09);  static style16x2() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 16,        tablet: 18,        desktop: 20,      ),      FontWeight.w700,      AppColors.white,      0.0);  static style14() => _interTextStyle(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 14,        tablet: 16,        desktop: 18,      ),      FontWeight.w500,      AppColors.charcoalGray,      0);  static TextStyle _interTextStyle(      double fontSize, FontWeight fontWeight, Color color, double height) {    return GoogleFonts.inter(      height: height,      fontSize: fontSize,      fontWeight: fontWeight,      color: color,    ).copyWith(overflow: TextOverflow.ellipsis);  }  static style22urbanist() => _urbanist(      ResponsiveDesignService.getInstance().getResponsiveFontSize(        mobile: 22,        tablet: 24,        desktop: 26,      ),      FontWeight.w700,      AppColors.deepGray,      0.0);  static TextStyle _urbanist(      double fontSize, FontWeight fontWeight, Color color, double height) {    return GoogleFonts.urbanist(      height: height,      fontSize: fontSize,      fontWeight: fontWeight,      color: color,    ).copyWith(overflow: TextOverflow.ellipsis);  }}