String? validatePassword(bool singIn, {required String password}) {  if (password.isEmpty) {    return "Please Enter Password";  }  else if (singIn) {    if (password.length < 8) {      return 'The password must be at least 8 characters long.';    }    else if (!password.contains(RegExp(r'[A-Z]'))) {      return 'The password must contain at least one uppercase letter.';    }    else if (!password.contains(RegExp(r'[a-z]'))) {      return 'The password must contain at least one lowercase letter.';    }    else if (!password.contains(RegExp(r'[0-9]'))) {      return 'The password must contain at least one digit.';    }    else if (RegExp(r'^[0-9]+$').hasMatch(password)) {      return 'The password must not contain only numbers.';    }  }  return null;}