String? validatePhone(String phone) {  const String pattern = r'(^(?:[+0]9)?[0-9]{10,12}$)';  final RegExp regex = RegExp(pattern);  // Asynchronous Firebase Firestore check for existing phone number  // final FirebaseFirestore firestore = FirebaseFirestore.instance;  // final CollectionReference usersCollection = firestore.collection('Users');  // final QuerySnapshot querySnapshot =  //     await usersCollection.where('phone', isEqualTo: phone).get();  if (phone.isEmpty) {    return "Please Enter Your Phone";  } else if (!regex.hasMatch(phone)) {    return 'The phone number is invalid. Please enter a valid 10-12 digit phone number.';  }  // else if (querySnapshot.docs.isNotEmpty) {  //   return 'The phone number is already registered.';  // }  return null;}