String? validateEmailOrPassword({String? input}) {  const String phonePattern = r'(^(?:[+0]9)?[0-9]{10,12}$)';  const String emailPattern =      r'^[\w-]+(\.[\w-]+)*@([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,7}$';  final RegExp phoneRegex = RegExp(phonePattern);  final RegExp emailRegex = RegExp(emailPattern);  if (input == null || input.isEmpty) {    return "Please enter your phone or email.";  }  if (RegExp(r'^[0-9]+$').hasMatch(input)) {    if (!phoneRegex.hasMatch(input)) {      return 'The phone number is invalid. Please enter a valid 10-12 digit phone number.';    }  } else {    if (!emailRegex.hasMatch(input)) {      return 'The email address is badly formatted.';    }  }  return null;}