import 'package:flutter/material.dart';import '../../constant/app_constants.dart';import '../../constant/colors.dart';import '../../constant/text_style.dart';class BuildCustomButton extends StatelessWidget {  final String text;  final void Function()? onPressed;  const BuildCustomButton({    super.key,    required this.text,    this.onPressed,  });  @override  Widget build(BuildContext context) {    return MaterialButton(      onPressed: onPressed,      color: AppColors.cornFlowerPrimary,      height: MediaQuery.orientationOf(context) == Orientation.portrait          ? MediaQuery.sizeOf(context).width * .125          : MediaQuery.sizeOf(context).height * .125,      minWidth: MediaQuery.sizeOf(context).width,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(AppConstants.defaultCircularBorder),      ),      child: Text(        text,        style: AppTextStyles.style16x2(),      ),    );  }}