import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../constant/app_constants.dart';import '../../constant/colors.dart';import '../../constant/text_style.dart';class BuildCustomButton extends StatelessWidget {  final String text;  final void Function()? onPressed;  const BuildCustomButton({    super.key,    required this.text,    this.onPressed,  });  @override  Widget build(BuildContext context) {    return MaterialButton(      onPressed: onPressed,      color: AppColors.cornFlowerPrimary,      height: .05.sh,      minWidth: 1.sw,      shape: RoundedRectangleBorder(        borderRadius: AppConstants.defaultCircularBorder,      ),      child: Text(        text ,        style: AppTextStyles.style16x2(),      ),    );  }}