import 'package:flutter/gestures.dart';import 'package:flutter/material.dart';import '../../constant/text_style.dart';class BuildTextButton extends StatelessWidget {  final String text1;  final String text2;  final void Function()? onPressedText2;  const BuildTextButton({    super.key,    required this.text1,    required this.text2,    this.onPressedText2,  });  @override  Widget build(BuildContext context) {    return Align(        alignment: Alignment.bottomCenter,        child: Padding(          padding: const EdgeInsets.only(bottom: 30),          child: RichText(            text: TextSpan(              children: [                TextSpan(                  text: text1,                  style: AppTextStyles.style16xW500(),                ),                TextSpan(                  text: text2,                  style: AppTextStyles.style16xW700cornFlowerPrimary(),                  recognizer: TapGestureRecognizer()..onTap = onPressedText2,                ),              ],            ),          ),        ),      );  }}