import 'dart:io';import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../core/services/pdf_util.dart';part 'extracted_text_state.dart';class ExtractedTextCubit extends Cubit<ExtractedTextState> {  ExtractedTextCubit() : super(ExtractedTextInitial());  Future<void> generateQuestion(      {required String text, required File fileSelected}) async {    emit(ExtractedTextLoading());    try {      await PdfUtil.createPdf(text: text, fileSelected: fileSelected);      emit(ExtractedTextSuccess());    } catch (e) {      emit(ExtractedTextFailure(error: e.toString()));    }  }}