import 'dart:io';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/constant/colors.dart';class ImagePicker extends StatelessWidget {  final File file;  const ImagePicker({    super.key,    required this.file,  });  @override  Widget build(BuildContext context) {    return Container(      constraints: BoxConstraints(maxHeight: 500, maxWidth: 500).w,      decoration: BoxDecoration(        border: Border.all(color: AppColors.redGary),        borderRadius: BorderRadius.circular(10.r),      ),      child: Image.file(        file,        fit: BoxFit.contain,      ),    );  }}