import 'package:flutter/material.dart';import '../../../../../core/constant/string.dart';import '../../../../../core/utils/Widget/build_text_button.dart';import '../Form/form_forget_password.dart';class BodyForgetPassword extends StatelessWidget {  const BodyForgetPassword({super.key});  @override  Widget build(BuildContext context) {    return CustomScrollView(      physics: const RangeMaintainingScrollPhysics(),      slivers: [        SliverToBoxAdapter(          child: FormForgetPassword(),        ),        SliverFillRemaining(          hasScrollBody: false,          child: BuildTextButton(            text1: AppString.rememberPassword,            text2: AppString.loginNow,            onPressedText2: () {              Navigator.pop(context);            },          ),        )      ],    );  }}