import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:textdetection/core/constant/app_constants.dart';import '../../../view_model/authentication/authentication_cubit.dart';import '../../../../../Config/Route/app_route.dart';import '../../../../../core/constant/string.dart';import '../../../../../core/utils/Widget/build_login_with_container.dart';import '../../../../../core/utils/Widget/build_text_button.dart';import '../custom_or_login_register_widget.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../Form/form_login.dart';class BodyLogin extends StatelessWidget {  const BodyLogin({super.key});  @override  Widget build(BuildContext context) {    return BlocConsumer<AuthenticationCubit, AuthenticationState>(      listener: (context, state) {},      builder: (context, state) {        return CustomScrollView(          physics: const RangeMaintainingScrollPhysics(),          slivers: [            SliverToBoxAdapter(              child: FormLogin(),            ),            SliverToBoxAdapter(              child: 32.verticalSpace,            ),            SliverToBoxAdapter(              child: CustomOrLoginRegisterWidget(                orWithText: Text(AppString.orLoginWith.toString()),              ),            ),            SliverToBoxAdapter(              child: BuildLoginWithContainer(),            ),            SliverFillRemaining(              hasScrollBody: false,              child: BuildTextButton(                text1: AppString.dontHaveAnAccount,                text2: AppString.registerNow,                onPressedText2: () {                  Navigator.pushNamed(context, AppRoutes.registerScreen);                },              ),            ),          ],        );      },    );  }}