import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/svg.dart';import 'package:textdetection/core/constant/text_style.dart';import '../../../../core/constant/app_constants.dart';import '../../../../core/constant/colors.dart';import '../../../../generated/assets.dart';AppBar backArrow(BuildContext context, {String? title, List<Widget>? actions}) {  return AppBar(    leadingWidth: 72.0.w,    leading: InkWell(      onTap: () {        Navigator.of(context).pop();      },      child: Container(        width: 19.w,        height: 19.h,        padding: EdgeInsets.all(7),        margin: EdgeInsets.only(left: 32.0, top: 7.0, bottom: 8.0).r,        clipBehavior: Clip.antiAlias,        decoration: BoxDecoration(          boxShadow: [            BoxShadow(              color: AppColors.blueLight,              blurRadius: .5,              blurStyle: BlurStyle.outer,            )          ],          color: Colors.white,          borderRadius: AppConstants.defaultCircularBorder,        ),        child: SvgPicture.asset(          Assets.arrow,          fit: BoxFit.contain,        ),      ),    ),    title: Text(      title ?? "",      style: AppTextStyles.style20(),    ),    centerTitle: true,    actions: actions,  );}