import 'package:flutter/material.dart';import '../../../../Config/Route/app_route.dart';import 'custom_text_form_field.dart';import '../../../../core/constant/string.dart';import '../../../../core/constant/text_style.dart';import '../../../../core/utils/Widget/build_default_button.dart';class FormForgetPassword extends StatefulWidget {  const FormForgetPassword({super.key});  @override  State<FormForgetPassword> createState() => _FormForgetPasswordState();}class _FormForgetPasswordState extends State<FormForgetPassword> {  final accountName = TextEditingController();  final GlobalKey<FormState> keyForm = GlobalKey<FormState>();  @override  Widget build(BuildContext context) {    return Form(      key: keyForm,      autovalidateMode: AutovalidateMode.onUserInteraction,      child: Padding(        padding: const EdgeInsets.only(left: 32.5, right: 32.5, top: 16),        child: Column(          children: [            Align(              alignment: Alignment.centerLeft,              child: Text(                AppString.forgotPassword,                maxLines: AppString.forgotPassword.length,                style: AppTextStyles.style30(),              ),            ),            SizedBox(height: 12.0),            Text(              AppString.forgotPasswordMassage,              style: AppTextStyles.style14(),              maxLines: AppString.forgotPasswordMassage.length,            ),            const SizedBox(              height: 32,            ),            CustomTextFormField(              controller: accountName,              hint: AppString.emailHint,            ),            Padding(              padding: const EdgeInsets.only(top: 32),              child: BuildCustomButton(                text: AppString.sendCode,                onPressed: () {                  Navigator.pushNamed(context, AppRoutes.otpScreen);                },              ),            )          ],        ),      ),    );  }}