import 'package:flutter/material.dart';import '../../../../core/constant/string.dart';import '../../../../core/constant/text_style.dart';import '../../../../core/utils/Widget/build_default_button.dart';import '../../../../core/utils/Widget/password_text__form_field.dart';import '../widgets/custom_text_form_field.dart';class FormLogin extends StatefulWidget {  const FormLogin({    super.key,  });  @override  State<FormLogin> createState() => _FormLoginState();}class _FormLoginState extends State<FormLogin> {  TextEditingController emailController = TextEditingController();  TextEditingController passwordController = TextEditingController();  final GlobalKey<FormState> keyForm = GlobalKey<FormState>();  @override  void initState() {    emailController.clear();    passwordController.clear();    super.initState();  }  @override  Widget build(BuildContext context) {    return SliverToBoxAdapter(      child: Form(        key: keyForm,        autovalidateMode: AutovalidateMode.onUserInteraction,        child: Padding(          padding: EdgeInsets.only(left: 32.5, right: 32.5, top: 57),          child: Column(            children: [              Text(                AppString.loginHeader,                maxLines: AppString.loginHeader.length,                style: AppTextStyles.style32(),              ),              SizedBox(                height: 32,              ),              CustomTextFormField(                hint: AppString.emailHint,                controller: emailController,              ),              SizedBox(                height: 12,              ),              CustomPasswordTextFromField(                controller: passwordController,              ),              SizedBox(                height: 5,              ),              Align(                alignment: Alignment.centerRight,                child: GestureDetector(                  onTap: () {                    // todo screens Forget                  },                  child: Text(                    AppString.forgotPassword,                    style: AppTextStyles.style16xW700(),                  ),                ),              ),              SizedBox(                height: 32,              ),              BuildCustomButton(                text: AppString.loginName,                onPressed: () {                  // todo go in splash screen                },              ),            ],          ),        ),      ),    );  }}