import 'package:flutter/material.dart';import 'package:textdetection/core/constant/app_constants.dart';import '../../../../core/constant/string.dart';import '../../../../core/constant/text_style.dart';import '../../../../core/utils/Widget/build_default_button.dart';import '../../../../core/utils/Widget/password_text__form_field.dart';import '../../../home/view/pages/home_screen.dart';import '../pages/create_new_password_screen.dart';import '../widgets/custom_text_form_field.dart';class FormLogin extends StatefulWidget {  const FormLogin({    super.key,  });  @override  State<FormLogin> createState() => _FormLoginState();}class _FormLoginState extends State<FormLogin> {  TextEditingController emailController = TextEditingController();  TextEditingController passwordController = TextEditingController();  final GlobalKey<FormState> keyForm = GlobalKey<FormState>();  @override  void initState() {    emailController.clear();    passwordController.clear();    super.initState();  }  @override  Widget build(BuildContext context) {    return Form(      key: keyForm,      autovalidateMode: AutovalidateMode.onUserInteraction,      child: Padding(        padding: AppConstants.customFormPadding,        child: Column(          children: [            Align(              alignment: Alignment.centerLeft,              child: SizedBox(                  width: 365,                  child: Text(                    AppString.loginHeader,                    maxLines: AppString.loginHeader.length,                    style: AppTextStyles.style30(),                  )),            ),            const SizedBox(              height: 32,            ),            CustomTextFormField(              hint: AppString.emailHint,              controller: emailController,            ),            const SizedBox(              height: 12,            ),            CustomPasswordTextFromField(              controller: passwordController,            ),            const SizedBox(              height: 5,            ),            Align(              alignment: Alignment.centerRight,              child: GestureDetector(                onTap: () {                  Navigator.of(context).push(                    MaterialPageRoute(                      builder: (context) => CreateNewPasswordScreen(),                    ),                  );                },                child: Text(                  AppString.forgotPassword,                  style: AppTextStyles.style16xW700(),                ),              ),            ),            const SizedBox(              height: 32,            ),            BuildCustomButton(              text: AppString.loginName,              onPressed: () {                Navigator.of(context).push(                  MaterialPageRoute(                    builder: (context) => HomeScreen(),                  ),                );              },            ),          ],        ),      ),    );  }}