import 'dart:ui';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../generated/assets.dart';import '../../../../core/constant/colors.dart';class ImageView extends StatelessWidget {  const ImageView({super.key, });  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: Stack(        children: [          InkWell(              onTap: () => Navigator.of(context).pop(),              splashColor: Colors.transparent,              highlightColor: Colors.transparent,              child: BackdropFilter(                filter: ImageFilter.blur(sigmaX: 10, sigmaY: 10),                blendMode: BlendMode.srcOver,                child: Container(                    color: AppColors.cream.withOpacity(0.3),                    child: Center(                        child: Padding(                      padding: const EdgeInsets.only(top: 40),                      child: Container(                        clipBehavior: Clip.antiAliasWithSaveLayer,                        decoration: BoxDecoration(                            shape: BoxShape.circle,                            border:                                Border.all(width: 3, color: AppColors.black)),                        child: CircleAvatar(                          radius: 85.r,                          backgroundImage: AssetImage(                            Assets.pngProfile,                          ),                        ),                      ),                    ))),              )),        ],      ),    );  }}