import 'dart:ui';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:textdetection/features/user/model/user_data_model.dart';import '../../../../generated/assets.dart';import '../../../../core/constant/colors.dart';class ImageView extends StatelessWidget {  const ImageView({    super.key,  });  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: Stack(        children: [          InkWell(              onTap: () => Navigator.of(context).pop(),              splashColor: Colors.transparent,              highlightColor: Colors.transparent,              child: BackdropFilter(                filter: ImageFilter.blur(sigmaX: 5, sigmaY: 5),                blendMode: BlendMode.srcIn,                child: Container(                    color: AppColors.cream.withOpacity(0.3),                    child: Center(                        child: Container(                      clipBehavior: Clip.antiAliasWithSaveLayer,                      decoration: BoxDecoration(                          shape: BoxShape.circle,                          border:                              Border.all(width: 4, color: AppColors.blueDark)),                      child: CircleAvatar(                        radius: 90.r,                        backgroundImage: AssetImage(                          // Assets.pngProfile,                          UserDataModel.imageUrl,                        ),                      ),                    ))),              )),        ],      ),    );  }}